mapper.insert:

	SELECT SUBSTR(MAX(PRODUCT_ID),0,1)
	       || TRIM(SUBSTR(MAX(PRODUCT_ID),2)+1)
	FROM PRODUCT

INSERT INTO PRODUCT(
	  PRODUCT_ID
	, PNAME
	, UNIT_PRICE
	, DESCRIPTION
	, MANUFACTURER
	, CATEGORY
	, UNIT_IN_STOCK
	, CONDITION
)
VALUES(
	#{productId}
	, #{pname}
	, #{unitPrice}
	, #{description}
	, #{manufacturer}
	, #{category}
	, #{unitInStock}
	, #{condition}
	)

mapper.update:
update product
set pname = #{pname}
    ,unit_price = #{unitPrice}
    ,description = #{description}
    ,manufacturer = #{manufacturer}
    ,category = #{category}
    ,unit_in_stock = #{unitInStock}
    ,condition = #{condition}
    ,filename = #{filename}
    ,quantity = #{quantity}
where 
    product_id = #{productId}
